
var controls=document.querySelectorAll('.advantages__controll');for(var i=0;i<controls.length;i++){controls[i].style.display='inline-block';}
var slides=document.querySelectorAll('.advantages__box');var currentSlide=0;function nextSlide(){goToSlide(currentSlide+1);}
function previousSlide(){goToSlide(currentSlide-1);}
function goToSlide(n){slides[currentSlide].className='advantages__box';currentSlide=(n+slides.length)%slides.length;slides[currentSlide].className='advantages__box  advantages__box--show';}
var next=document.querySelector('.advantages__controll--next');var previous=document.querySelector('.advantages__controll--prev');next.onclick=function(){nextSlide();};previous.onclick=function(){previousSlide();};